package javaprograms;

public class DoWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//syntax of do while loop 
		// print a ststement a  ststement 7 times, if condition id true
		//if cond. id not true , even then stat. atlest once.
		
		/* do{
		 * 
		 * loop statements
		 * operations
		 * increments operation
		 * }
		 * while(condition)
		 * 
		 */
		
		int i=1;
		
		do {
			
		
		System.out.println("Today is wednesday");
		i++;//increment the values of i by 1
		
		
		}
		while(i<=7);
		
		
		System.out.println("Printed the messages. I am out of Loop");
		
		
	}
	

}
