package javaprograms;

public class InbuiltMethods {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// examples how to use inbuilt methods of string class
		
		String s1 = "Seleneium ";
		String s2 = "training" ;
		// concatinate both string and print it
		
		//using + operator
		
		
		String s3 = s1+s2;  //selenium training
		
		String s4 =s1.concat(s2); //selenium training
		
		System.out.println("concatition using + operator :"+  s3);
		
		System.out.println("concatition using concat method :"+  s4);
	
		

	}

}
